$(document).ready(function(){$("#claim-type").change(function(e){var a=$(this).val(),i=$("#table-medical"),s=$(".table-prescription"),l=s.closest(".container--base").find(".loading-block");switch(a){case"medical":l.show(),s.hide(),i.show();break;default:l.show(),i.hide(),s.show()}setTimeout(function(){l.hide()},300)}),$("#provider-type").change(function(e){var a=$(this).val();$(".provider-result").html("20 claims from "+a)}),$("[id*=date-range]").change(function(e){var a=$(this).val();"30 days"===a?($(".claim-results").hide(),$(".claim-no-results").removeClass("display-none")):($(".claim-results").show(),$(".claim-no-results").addClass("display-none"))})});